import React from 'react';
import './AccommodationBlock.less';
import { Badge, Carousel, Image } from 'antd';
import _ from 'lodash';
import { CaretRightOutlined, CaretLeftOutlined } from '@ant-design/icons';
import OwnerInfoBlock from './OwnerInfoBlock/OwnerInfoBlock';
import SpaceBlock from './SpaceBlock/SpaceBlock';

type AccommodationType = {
  accommodation: {
    id: number,
    price: number,
    description: string,
    title: string,
    floor: number,
    maxFloor: number,
    roomCount: number,
    address: string,
    space: number,
    metro: {
      name: string,
      color: string,
      transportTime: number,
      transportType: string,
    }[]
    images:{ src: string }[]
    owner: {
      name: string,
      phoneNumber: string,
    }
  };
};

// const NextArrow = ({currentSlide, slideCount, ...props}: CustomArrowProps) => (
//   <div {...props}>
//     <i className="fas fa-arrow-circle-right"></i>
//   </div>
// );

const AccommodationBlock: React.FC<AccommodationType> = ({ accommodation }: AccommodationType) => {
  const {
    price, description, title, floor, maxFloor, roomCount, address, space, images, owner, metro,
  } = accommodation;
  return (
    <div className="accommodation-view">
      <div className="image-block">
        <Carousel arrows dotPosition="top" nextArrow={<CaretRightOutlined />} prevArrow={<CaretLeftOutlined />}>
          {images.map((image) => (
            <div key={image.src}>
              <Image height={500} key={image.src} src={image.src} />
            </div>
          ))}
        </Carousel>
      </div>
      <SpaceBlock space={space} floor={floor} maxFloor={maxFloor} roomCount={roomCount} />
      <OwnerInfoBlock owner={accommodation.owner} />
    </div>
  );
};

export default AccommodationBlock;
